mapper.select:
SELECT * FROM (SELECT ROWNUM RNUM, PROD.* FROM (SELECT * FROM product WHERE prod_display = 1 AND prod_stock > 0
AND prod_name LIKE '%'||#{keyword}||'%'
ORDER BY prod_no DESC) PROD) WHERE RNUM BETWEEN #{listStart} AND #{listEnd}

mapper.update:
UPDATE product
SET cate_no = #{cate_no}, prod_name = #{prod_name}, prod_price = #{prod_price}, 
	prod_content = #{prod_content}, prod_stock = #{prod_stock}, prod_display = #{prod_display}
WHERE prod_no = #{prod_no}
